/*
 * You are given two strings s and t. String t is generated by random shuffling string s and then add one more letter at a random position.
 * Return the letter that was added to t.
 * 
 * Input: s = "abcd", t = "abcde"
 * Output: "e"
 * Explanation: 'e' is the letter that was added.
 * 
 * Input: s = "", t = "y"
 * Output: "y"
 * 
 */

package LeetCode;

import java.util.HashMap;
import java.util.Map;

public class FindTheDifferenceFromTwoStrings {
    public static void main(String[] args) 
    {
        // String s = "abcd", t = "abcde";
        // String s = "", t = "y";

        String s = "";
        String t = "";
        System.out.println("The char is : " + findTheDifference(s, t));
    }

    static char findTheDifference(String s, String t) 
    {
        Map<Character, Integer> s1 = new HashMap<>();
        Map<Character, Integer> s2 = new HashMap<>();
        for (int i = 0; i < s.length(); i++) {
            if(s1.containsKey(s.charAt(i)))
            {
                int val = s1.get(s.charAt(i));
                val = val + 1;
                s1.put(s.charAt(i), val);
            }
            else
            {
                s1.put(s.charAt(i), 1);
            }
        }
        for (int i = 0; i < t.length(); i++) {
            if(s2.containsKey(t.charAt(i)))
            {
                int val = s2.get(t.charAt(i));
                val = val + 1;
                s2.put(t.charAt(i), val);
            }
            else
            {
                s2.put(t.charAt(i), 1);
            }

        }
        for (int i = 0; i < t.length(); i++) {
            if(s1.get(t.charAt(i)) == s2.get(t.charAt(i)))
            {
                System.out.println("It went inside");
            }
            else
            {
                return t.charAt(i);
            }
        }
        return ' ';
    }
}
